/*Table structure for table `dept` */

DROP TABLE IF EXISTS `dept`;

CREATE TABLE `dept` (
  `deptNum` smallint(10) NOT NULL auto_increment COMMENT '系号码',
  `deptName` varchar(20) NOT NULL COMMENT '系名称',
  `deptChairman` varchar(10) NOT NULL COMMENT '系主任',
  `telephone` varchar(15) default '' COMMENT '联系方式',
  `description` text COMMENT '系介绍',
  PRIMARY KEY  (`deptNum`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

/*Data for the table `dept` */

insert  into `dept`(`deptNum`,`deptName`,`deptChairman`,`telephone`,`description`) values (1,'自动化学院','谢胜利','','广东工业大学自动化学院坐落于美丽的广州大学城校区，由1958年创办的原广东工学院电工系发展而来。学院现已形成了本科、硕士研究生、博士研究生和博士后的人才培养体系,已培养毕业生两万余人。学院设有自动化、电气工程及其自动化（含电气与电子技术、电力系统自动化两个方向）、物联网与大数据工程、电子信息科学技术四个本科专业。'),(2,'外国语学院','蒋澄生','','外国语学院的前身是广东工业大学合并前的广东机械学院外语系，1993年3月创办，并于同年9月起招收英语专科生；1995年9月起招收英语本科生；2002年9月起英语本科专业按经贸和翻译两个方向招生，2003年9月起招收日语专业本科生，2003年11月外国语学院成立。2012年9月起开设商务英语专业和英语专业（科技方向）。2014年获准设置翻译本科专业，2014年开始招生。2014年获准设置翻译硕士学位点，2015年开始招生。外国语学院设英语系、日语系、翻译硕士（MTI）教育中心、大学英语一部、大学英语二部五个教学单位和一个研究机构──外国语言文化研究中心。'),(3,'计算机学院','凌捷','','计算机学院本科相关专业建设已有30多年的办学历史，涵盖计算机科学与技术、软件工程和信息安全三个学科。现有1个二级学科博士学位授权点，2个一级学科硕士学位授权点，2个工程硕士培养领域授权点；有1个广东省优势重点学科，1个省级名牌专业；拥有完整的从学士到博士的人才培养体系。'),(4,'信息工程学院','章国豪','','信息工程学院座落在广东工业大学大学城校园工学一号馆，建筑面积6522平方米，是广东省重要的IT人才培养基地。学院设有通信工程系、电子信息工程系、应用电子技术系和学院实验中心等四个基础教学单位。学院现有在校本科生2959人、硕士研究生308人。');

/*Table structure for table `major` */

DROP TABLE IF EXISTS `major`;

CREATE TABLE `major` (
  `majorNum` char(10) NOT NULL COMMENT '专业号码',
  `deptNum` smallint(6) NOT NULL COMMENT '系号码',
  `majorName` varchar(20) NOT NULL COMMENT '专业名',
  `majorAssistant` varchar(10) default '' COMMENT '辅导员',
  `telephone` varchar(15) default '' COMMENT '联系方式',
  `description` text COMMENT '专业介绍',
  PRIMARY KEY  (`majorNum`),
  KEY `major-dept` (`deptNum`),
  CONSTRAINT `major-dept` FOREIGN KEY (`deptNum`) REFERENCES `dept` (`deptNum`)
